import './App.css';
import React, { useState, useRef, useEffect } from 'react';
import Modal from './components/Modal';
import { v4 as uuidv4 } from 'uuid';

function App() {
  const [secret, setSecret] = useState('');
  const [code, setCode] = useState('');
  const [vis, setVis] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const inputRef = useRef();
  const changeFormValue = (e) => {
    setSecret(e.target.value);
  };
  const handleSubmit = (e) => {
    if (secret) {
      const random = uuidv4();
      //upload key
      //upload secret
      //if response is ok, give code and set code
      setCode(random);
      setModalContent('Your code is: ' + random);
      setVis(true);
      setSecret('');
    } else {
      setModalContent('Enter your secret.');
      setVis(true);
    }
    e.preventDefault();
  };
  useEffect(() => {
    inputRef.blur();
  }, [vis]);
  return (
    <div className='body'>
      <span className='header'>SECRETS</span>
      <div className='content'>
        {code ? (
          <div className='code'>
            <span>click to copy</span>
            <span>â†“</span>
            <span>
              <button
                className='buttonAsText'
                onClick={() => {
                  navigator.clipboard.writeText(code);
                  setModalContent('Successfully copied.');
                  setVis(true);
                }}
              >
                CODE: {code && code}
              </button>
            </span>
          </div>
        ) : (
          <span>ENTER SECRET: </span>
        )}
        <form onSubmit={(e) => handleSubmit(e)}>
          <input ref={inputRef} value={secret} onChange={changeFormValue} />
          <button type='submit'>Submit</button>
        </form>
        <Modal vis={vis} setVis={setVis} content={modalContent} />
      </div>
    </div>
  );
}

export default App;
