import './App.css';
import React, { useState } from 'react';
import Modal from './components/Modal';
import { v4 as uuidv4 } from 'uuid';

function App() {
  const [secret, setSecret] = useState('');
  const [code, setCode] = useState('');
  const [vis, setVis] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const changeFormValue = (e) => {
    setSecret(e.target.value);
  };
  const handleSubmit = (e) => {
    if (secret) {
      const random = uuidv4();
      //upload key
      //upload secret
      //if response is ok, give code and set code
      setCode(random);
      setModalContent('Your code is: ' + random);
      setVis(true);
      setSecret('');
    } else {
      setModalContent('Enter your secret.');
      setVis(true);
    }
    e.preventDefault();
  };
  navigator.clipboard.writeText('suspicious');
  return (
    <div className='body'>
      {/* <img src={logo} className='App-logo' alt='logo' /> */}
      <span className='header'>SECRETS</span>
      <div className='content'>
        <span>
          <button
            className='buttonAsText'
            aria-label='bruh sus'
            aria-labelledby='bruh sus'
          >
            CODE: {code && code}
          </button>
        </span>
        <form onSubmit={(e) => handleSubmit(e)}>
          <input value={secret} onChange={changeFormValue} />
          <button type='submit'>Submit</button>
        </form>
        <Modal vis={vis} setVis={setVis} content={modalContent} />
      </div>
    </div>
  );
}

export default App;
