import React from 'react';
import './index.css';
const classnames = require('classnames');

export default function Modal({ vis, setVis, content }) {
  return (
    <div className={classnames('modal', { none: !vis })}>
      <div
        className='overlay'
        onClick={() => {
          setVis(false);
        }}
      />
      <div className='box flex center'>
        <span>{content}</span>
        <button onClick={() => setVis(false)}>[x]</button>
      </div>
    </div>
  );
}
