import React from 'react';
import empty from '../../empty.webp';

function RightBlock({ img, title, children, bg = 'rgba(0,0,0,0)' }) {
  return (
    <div className='_block inherit my-3 lg:grid grid-cols-5 lg:grid-column grid-row justify-center'>
      <img
        className='w-full blockimg lazyload object-cover mx-auto col-start-1 col-end-3'
        src={empty}
        data-src={img}
        alt='Block'
      />
      <div className='content col-start-3 col-end-6 sm:mx-14 mt-6'>
        <h3 className='flex justify-end'>{title}</h3>
        <p className='text-2xl'>{children}</p>
      </div>
    </div>
  );
}

export default RightBlock;
